import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Account } from 'src/app/models/account.model';
import { AccountService } from 'src/app/services/account.service';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.scss']
})
export class AccountComponent implements OnInit {
    accountForm!: FormGroup;
    accounts: Account[] = [];
    
    
    constructor(private accountService: AccountService, private fb: FormBuilder){}
    

    showAccount() {
    this.accountService.getAccounts().subscribe({
      next: data => {
        this.accounts = data;
        console.log('Accounts received:', this.accounts);
      },
      error: err => {
        console.error('Failed to fetch accounts:', err);
      }
    });
  }

    createAccount(){
      if (this.accountForm.valid){
        const account: Account = this.accountForm.value;
        console.log('New Account: ', account);
        this.accountService.createAccount(account).subscribe(
          response => console.log('Account created Successfully:', response),
          error => console.error('Error creating account: ', error)
        );
      }
    }

    ngOnInit(): void {
        // this.showAccount();
        this.accountForm = this.fb.group({
        accountHolderName: ['', Validators.required],
        balance: 0, 
        userId: 1,
        transactions: [[]],
      });
    }

}
<div class="max-w-xl mx-auto mt-10 bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
  <h2 class="text-2xl font-semibold text-gray-800 mb-6">Create New Account</h2>

  <form [formGroup]="accountForm" (ngSubmit)="createAccount()" class="space-y-5">

    <!-- Account Holder Name -->
    <div>
      <label for="accountHolderName" class="block text-sm font-medium text-gray-700 mb-1">
        Account Holder Name
      </label>
      <input
        type="text"
        id="accountHolderName"
        formControlName="accountHolderName"
        placeholder="e.g. Jane Doe"
        class="w-full px-4 py-2 border rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
      />
      <p *ngIf="accountForm.get('accountHolderName')?.invalid && accountForm.get('accountHolderName')?.touched"
         class="text-red-500 text-sm mt-1">
        Name is required.
      </p>
    </div>

    <!-- Submit Button -->
    <button
      type="submit"
      [disabled]="accountForm.invalid"
      class="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-lg transition"
    >
      Create Account
    </button>

  </form>
</div>
