import { Component, OnInit } from '@angular/core';
import { Account } from 'src/app/models/account.model';
import { AccountService } from 'src/app/services/account.service';
@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.scss']
})
export class AccountComponent implements OnInit {

    constructor(private accountService: AccountService){}
    accounts: Account[] = [];

    showAccount() {
    this.accountService.getAccounts().subscribe({
      next: data => {
        this.accounts = data;
        console.log('Accounts received:', this.accounts);
      },
      error: err => {
        console.error('Failed to fetch accounts:', err);
      }
    });
  }

    createAccount(){

    }

    ngOnInit(): void {
        this.showAccount();
    }

}
<h1>Accounts</h1>

<div *ngFor="let account of accounts">
    <div> Name: {{account.accountHolderName}} </div>
    <div> Balance: R{{account.balance}} </div>
    <div *ngFor="let transaction of account.transactions">
        <div> Transaction Amount: {{transaction.amount}}</div>
        <div> Transaction Type: {{transaction.type}}</div>
    </div>
</div>
