import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent {

}
<footer class="fixed bottom-0 left-0 w-full bg-gray-100 border-t border-gray-200 shadow-sm z-50">
  <div class="mx-auto max-w-7xl px-6 py-4 lg:px-8 flex flex-col md:flex-row items-center justify-between text-sm text-gray-500">
    <span>© 2025 BankEase. All rights reserved.</span>

    <div class="flex space-x-4 mt-2 md:mt-0">
      <a href="#" class="hover:text-indigo-600 transition">Privacy</a>
      <a href="#" class="hover:text-indigo-600 transition">Terms</a>
      <a href="#" class="hover:text-indigo-600 transition">Support</a>
    </div>
  </div>
</footer>
